class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();

        // 1 2 3 4 5 1 2 3 4

        for (int i = 0; i < n; i++) {
            c.push_back(c[i]);
        }

        vector<int> dp(2 * n + 5, 0);
        dp[0] = 1;
        for (int i = 1; i < 2*n; i++) {
            dp[i] = 1;
            if (c[i] != c[i - 1]) {
                dp[i] += dp[i - 1];
            }
        }

        int g = n;
        int ans = 0;
        for (int i = k - 1; g > 0; i++, g--) {
            if (dp[i] >= k) {
                ans++;
            }
        }

        return ans;
    }
};
