class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int kp = 1, ans=0, n=colors.length;
        int i = 1;
        while(i<(n+k-1)){
            if(colors[i%n] != colors[(i-1)%n]){
                kp++;
            }
            else{
                kp=1;
            }
            if(kp>=k){
                ans++;
            }
            i++;
        }
        return ans;
    }
}
