class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int cnt=1,j=2,ans=0,n=a.size();
        for(int i=1;j<=n+k-1;i++){
             if(i>=n)i=0;
            if(i!=0 && a[i]==a[i-1])cnt=0;
            else if(i==0 && a[i]==a[n-1]){cnt=0;break;}
            cnt++;
             if(cnt>=k)ans++;

            j++;

        }
        return ans;




    }
};
