class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        colors.insert(colors.end(), colors.begin(), colors.begin() + k -1);

        int ans = 0;
        int num = 1;

        for (size_t a = 1; a < colors.size(); ++a) {
            num = (colors[a]!=colors[a-1]) ? num + 1 : 1;
            if (num>=k){
                ++ans;
            }
        }
        return ans;
    }
};
