class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors += colors
        n = len(colors)
        f = [1] * n
        res = 0
        for i in range(1 , n // 2 + k - 1):
            if colors[i] != colors[i - 1]:
                f[i] = f[i - 1] + 1
            if f[i] >= k:
                res += 1        
        return res
