class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        for(int i=0;i<n;i++){
            c.push_back(c[i]);
        }
        // for(auto it:c){
        //     cout<<it<<" ";
        // }
        // cout<<"\n";
        // return 0;
        int l=0,r=1;
        int ans=0;
        int ct=0;
        while(l<n && r<2*n){
            int m=r-l+1;
            if(c[r]!=c[r-1]){
                if(m==k){
                    ans++;
                    l++;
                    r++;
                }
                else{
                    r++;
                }
            }
            else{
                l=r;
                r++;
            }
//             if(m==k){
//                 ans++;
//                 l++;
//                 r++;
//             }
//             else if(m<k){

//             }
        }
        return ans;
    }
};
