class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& cols, int a) 
    { 
        int num = cols.size(); 
        int tot = 0; 
        if (a == 1) 
        { 
            return num; 
        } 
        int al= 1; 
        int i = 1; 
        while (i < num + a - 1) 
        { 
            int ct = i; 
            int pres = i - 1; 
            if (ct >= num) 
            { 
                ct -= num; 
            } 
            if (pres >= num) 
            { 
                pres -= num; 
            } 

            if (cols[ct] != cols[pres])
            { 
                ++al; 
            } else { 
                al = 1; 
            } 

            if (al>= a) { 
                ++tot; 
            } 
            ++i; 
        } 

        return tot; 
    } 
};
