class Solution:
    def numberOfAlternatingGroups(self, colors, k: int) -> int:
        n = len(colors)
        colors = colors + colors
        last = n + n - n % k - 1
        dp = [0] * (last + 1)
        dp[last] = 1
        for i in range(last - 1, -1, -1):
            if colors[i] != colors[i + 1]:
                dp[i] = dp[i + 1] + 1
            else:
                dp[i] = 1
        res = 0
        for i in range(n):
            if dp[i] >= k:
                res += 1
        return res
