class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        for (int i=0;i<n;i++) a.push_back(a[i]);
        int ans=0;
        int f=0;
        for (int i=0;i<n+k-1;i++){
            if (i-1>=0 && a[i-1]==a[i]) f++;
           // cout<<i<<" "<<f<<"\n";
            if (i>=k-1){
             //   cout<<ans<<"\n";
                if (!f) ans++;
            }
            if (i>=k-1 && a[i-(k-1)]==a[i-(k-1)+1]) f--;
            // if (n==k && i>=k-1 && a[i]==a[i-(k-1)]) f--;
            // cout<<i<<" -> "<<f<<"\n";
        }
        return ans;
    }
};
