class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n=arr.size();
        vector<int> pre(n,0);
        int curr=0;
        for(int i=0;i<n;i++)
        {
            if(arr[i]==arr[(i+1)%n])
                curr++;
            pre[i]=curr;
        }
        // for(auto i:pre)
        //     cout<<i<<" ";
        int ans=0;
        for(int i=0;i<n;i++)
        {
            // if(k==n)
            // {
            //     if(pre[n-1]==0)
            //         ans++;
            //     continue;
            // }
            if(i+k-1<=n-1)
            {
                int idx=i+k-1;
                int temp=0;
                if(idx-1>=i)
                    temp=pre[idx-1];
                if(i-1>=0)
                    temp-=pre[i-1];
                if(temp==0)
                    ans++;
                continue;
            }
            int temp=pre[n-1];
            if(i-1>=0)
                temp-=pre[i-1];
            int ele=n-i;
            ele=k-ele;
            int idx=ele-1;
            if(idx-1>=0)
                temp+=pre[idx-1];
           // cout<<ele<<endl;
            if(temp==0)
                ans++;
        }
        return ans;


    }
};
