class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        int cnt = 0;
        int curr = 0;
        for (int i = 0; i < k - 1; ++i) {
            if (v[i] != v[(i + 1) % n]) curr++;
        }
        if (curr == k - 1) cnt++;
        for (int i = 1; i < n; ++i) {
            int a = (i - 1) % n;
            int end = (i + k - 2) % n;
            int b = (i + k - 1) % n;
            if (v[a] != v[(a + 1) % n]) {
                curr--;
            }
            if (v[end] != v[b]) curr++;
            if (curr == k - 1) cnt++;
        }
        return cnt;
    }
};
