class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        for(int i=0;i<n;++i)
        {
            c.push_back(c[i]);
        }
        vector<int>pref(2*n+1);
        for(int i=0;i<2*n;++i)
        {
            pref[i] = (i ? pref[i-1] : 0) + (i && (c[i]!=c[i-1]));
        }
        // for(auto it:pref)cout<<it<<" ";
        cout<<endl;
        int ans = 0;
        for(int i=0;i<n;++i)
        {
            int l = i;
            int r = i+k-1;
            int sum = pref[r] - pref[l];
            if(sum == k-1)++ans;
        }
        return ans;
    }
};
