class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count = 0;
        int n = colors.size();
        int left = 0, right = 1;
        while(right<n+k-1){
            if(colors[right%n] != colors[(right-1) % n]){
                right++;
                if(right-left == k){
                    count++;
                    left++;
                }
            }
            else{
                left = right;
                right++;
            }
        }
        // if(n>=3){
        //     if(colors[n-2] == colors[0] && colors[n-1] != colors[0])count++;
        //     if(colors[n-1] == colors[1] && colors[0] != colors[1])count++;
        // }
        // if(n > 3){
        //     if(colors[n-1] == colors[1] && colors[0] != colors[1])count++;
        // }

        return count;
    }
};
