#define f(i,a,b) for(int i(a);i<b;i++)
class Solution {

public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int ans=0;int tot=0;
        f(i,0,k-1){
            if(colors[i]==colors[(i+1)]) tot++;
        }
        if(tot==0) ans++;
        f(i,1,n){
            if(colors[i]==colors[i-1]) tot--;
            int nxt=(i+k-1+n)%n;
            if(colors[nxt]==colors[(nxt-1+n)%n]) tot++;
            if(tot==0) ans++;
        }
        return ans;
    }
};
