class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0;
        int temp=0;
        int n=colors.size();
        auto diff=[&](int a,int b)
        {
            return colors[a%n]!=colors[b%n];
        };
        for(int i=0;i<k-1;i++)
        {
            if(diff(i,i+1))
            {
                temp++;
            }
        }
        if(temp==k-1)
        {
            ans++;
        }
        for(int i=1;i<colors.size();i++)
        {
            if(diff(i-1,i))
            {
                temp--;
            }
            if(diff(i+k-2,i+k-1))
            {
                temp++;
            }
            if(temp==k-1)
            {
                ans++;
            }
        }
        return ans;
    }
};
