class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& A, int k) {
    int n = A.size();
    for (int i = 0; i < k-1; i++) A.push_back(A[i]);
    int ans = 0;
    for (int i = 0, j = 0; i < A.size(); i=j) {
      j = i+1;
      while (j<A.size() && A[j] != A[j-1]) j++;
      ans += max(0,  j-i-k+1);
    }
    return ans;
  }
};
