class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), ans = 0;
        vector<int> l(n), x(n);
        for(int i=0;i<n;i++) {
            if(i>=2 && colors[i]==colors[i-2]) l[i] = l[i-2]+1;
            else l[i] = 1; 
        }
        x[0]=1;
        for(int i=0;i<n;i++){
            if(i){
                if(colors[i]!=colors[i-1]){
                    if(l[i-1]>=l[i]) x[i] = l[i]*2;
                    else x[i]=l[i-1]*2+1;
                }
                else x[i] = 1;
            }
        }
        for(int i=0;i<n;i++){
            if(k>i+1){
                if(colors[n-1]!=colors[0] && x[i]>=i+1 && x[i]+x[n-1]>=k) ans++;
            }
            else if(x[i]>=k) ans++;
        }
        return ans;
    }
};
