class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        N=len(colors)
        i=0
        j=1
        ans=0
        while j<N+k-1:
            curr=j%N
            prev=(N+j-1)%N
            # print([i,j], colors[prev], colors[curr])
            if colors[prev]!=colors[curr]:
                # pass
                pass
            else:
                if j-i-(k-1)>0:
                    ans+=j-i-(k-1)
                i=j
            j+=1
        if j-i-(k-1)>0:
            ans+=j-i-(k-1)
        return ans
