class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        cnt, res = 1, 0
        m = len(colors)
        for i in range(1, m+k-1):
            if colors[i%m] ^ colors[(i-1)%m]:
                cnt += 1
                if cnt >= k:
                    res += 1
            else:
                cnt = 1
        return res
