class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        colors.insert(colors.end(), colors.begin(), colors.end());
        int len = 1, ans = 0;
        for (int i = 1; i < k + n - 1; ++i) {
            if (colors[i] != colors[i - 1]) ++len;
            else len = 1;
            if (len >= k) ++ans;
        }
        return ans;
    }
};
