class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0,l=0,r=0;
        for(int i=0;i<k-1;i++)
            colors.push_back(colors[i]);
        while(r<colors.size())
        {
            while(r+1<colors.size() && colors[r]!=colors[r+1])
            {
                r++;
            }
            ans+=(max(0,r-l+1-(k-1)));
            r++;
            l=r;
        }
        return ans;
    }
};
