class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int ans=0;
        if(k==1)return n;
        int temp=1;
        int i=1;
        while(i<n+k-1)
        {
            int cur=i,prev=i-1;
            if(cur>=n)cur-=n;
            if(prev>=n)prev-=n;
            if(colors[cur]!=colors[prev])
            {
                temp++;
            }
            else temp=1;
            if(temp>=k)
                ans++;
            i++;
        }
        return ans;

    }
};
