class Solution {
public:
    int solve(vector<int>& colorArray, int k){
        vector<int> freq;
        for(int elem : colorArray) freq.push_back(elem);
        for(int i=0;i<k-1;i++) freq.push_back(colorArray[i]);
        int result = 0;
        for(int idx=0;idx<freq.size();idx++) {
        int seqCount = 1;
        for(int jdx=idx+1;jdx<freq.size();jdx++) {
        if(freq[jdx] != freq[jdx - 1]) seqCount++;
        else {
            idx = jdx - 1;
            break;
        }
        if(jdx == freq.size() - 1) {
            idx = 3 * freq.size();
            break;
        }
    }
    result += max(0, seqCount - k + 1);
}
return result;

    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        return solve( colors,k);
    }
};
