class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        Deque<Integer> deque = new LinkedList<>();
        int ans = 0;
        deque.offer(colors[0]);
        int n = colors.length;
        for(int i=1;i<colors.length+k-1;i++){
            if(colors[i%n]!=deque.peekLast()){
                deque.offer(colors[i%n]);
            } else {
                while (!deque.isEmpty()) deque.poll();
                deque.offer(colors[i%n]);
            }
            if(deque.size() == k){
                deque.poll();
                ans+=1;
            }
        }
        return ans;
    }
}
