class Solution {
public:
    #define ll long long
    ll p=331;
    ll mod=1e9+7;
    int numberOfAlternatingGroups(vector<int>& color, int k) {
        string s="";
        for(auto x: color){
            s+=to_string(x);
        }
        for(auto x: color){
            s+=to_string(x);
        }
        string s1="";
        string s2="";
        for(int i=0;i<k;i++){
            if(i%2==0){
                s1+="0";
            }
            else{
                s1+="1";
            }
        }
        for(int i=0;i<k;i++){
            if(i%2==0){
                s2+="1";
            }
            else{
                s2+="0";
            }
        }
        //now match it

        ll n=s.length();
        vector<ll> power(n+1);
        power[0]=1;
        for(ll i=1;i<=n;i++){
            power[i]=(power[i-1]*p)%mod;
            //power[i-1]=p^i-1 p^i 
        }
        //hash i->j //a j+1->j+1+(j-i+1)
        //a+a
        vector<ll> hash(n);
        //hash[i]=hash[0----i]
        ll h=0;
        for(ll i=0;i<n;i++){
            h=(h*p + (s[i]-'1'+1))%mod;
            hash[i]=h;
            //0---i =h
        }
        ll hash1=0;
        for(int i=0;i<s1.length();i++){
            hash1=(hash1*p + (s1[i]-'1'+1))%mod;

        }
        ll hash2=0;
        for(int i=0;i<s2.length();i++){
            hash2=(hash2*p + (s2[i]-'1'+1))%mod;
        }
        int cnt=0;
        for(int i=0;i<color.size();i++){
            //hash from i to j;
            int j=i+k-1;
            if(j>=n)continue;
            ll hashed=(hash[j]-(i-1<0 ? 0 : hash[i-1]*power[j-i+1]) + mod*mod)%mod;

            if(hashed==hash1 or hashed==hash2){
                cnt++;
            }
        }
        return cnt;

    }
};
