class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
           colors.insert(colors.end(), colors.begin(), colors.begin() + k); 

            int n = colors.size(), ans = 0; 

            vector<int> v(n + 1, 0); 

            for (int i = 1; i < n; i++) 
            { 
                v[i] += v[i - 1] + abs(colors[i]-colors[i-1]); 
            } 
            k--; 
            for (int i = k+1; i < n; i++) 
            { 
                if (v[i] - v[i - k] == k ) { 
                    ans++; 
                } 
            } 
            return ans; 
    }
};
