typedef long long ll;
int mod = 1e9 + 7;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {

        int n = a.size();

        for(int i = 0; i < k - 1; ++i){
            a.push_back(a[i]);
        }

        // for(int i = 0; i < a.size(); ++i){
        //     cout << a[i] << " ";
        // }
        // cout << "\n";

        ll h1 = 0;
        ll h2 = 0;

        ll multiplier = 1;

        for(int i = 0; i < k; ++i){
            if(i % 2 == 0){
                h1 += multiplier;
            }
            else{
                h2 += multiplier;
            }

            h1 %= mod;
            h2 %= mod;

            multiplier *= 2ll;
            multiplier %= mod;
        }

        ll cans = 0;
        multiplier = 1ll;
        for(int i = 0; i < k; ++i){
            cans += (a[i] * multiplier) % mod;
            cans %= mod;

            multiplier *= 2;
            multiplier %= mod;
        }
        // cout << cans << " ";
        int cnt = 0;
        if(cans == h1 || cans == h2) ++cnt;

        for(int i = k; i < a.size(); ++i){
            cans += (a[i] * multiplier) % mod;
            cans %= mod;

            cans -= (a[i - k] % mod);
            cans += mod; cans %= mod;

            cans *= 500000004ll;
            cans %= mod;
            // cout << cans << " ";
            if(cans == h1 || cans == h2){
                ++cnt;
            }
        }
        // cout << "\n";

        return cnt;

    }
};
