class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) 
    {
        int n = a.size();
        int pre[n];
        pre[0] = 0;
        for(int i = 1; i < n; i++) pre[i] = pre[i - 1] + (a[i] != a[i - 1]);
        int ans = 0;
        for(int i = 0; i < n; i++)
        {
            if (i + k - 1 < n)
            {
                int x = pre[i + k - 1];
                x -= pre[i];
                if (x == k - 1) ans++;
                // cout << x << ' ';
            }
            else
            {
                int x = pre[n - 1];
                x -= pre[i];
                if (x != n - 1 - i) continue;
                // cout << i << ' ';
                if (a[n - 1] == a[0]) continue;
                // cout << i << ' ';
                // cout << r << ' ';
                int r = k - n + i;
                // cout << r << ' ';
                // cout << i << ' ' << r << '\n';
                if (pre[r - 1] == r - 1) ans++;
            }
            // cout << i << ' ';
        }
        return ans;
    }
};
