class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i=0; i<k-1; i++)v.push_back(v[i]);
        int c = 1;
        vector<int> pr;
        pr.push_back(1);

        for(int i=1; i<v.size(); i++){
            if(v[i]!=v[i-1]){
                pr.push_back(c+1);
                c++;
            }
            else{
                pr.push_back(1);
                c=1;
            }
        }
        int ans =0;
        for(int i=0; i<pr.size(); i++){
            if(pr[i]>=k)ans++;
        }
        return ans;
    }
};
