class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        set<int> st;
        int n=a.size();
        for(int i=1;i<n;i++){
            if(a[i]==a[i-1])
            st.insert(i);
        }
        if(a.back()==a[0])
        st.insert(0);
        int j=0,cnt=0,ans=0;
        for(int i=0;j<n;i++){
            int ind=i%n;
            if(st.count(ind))
            cnt++;
            if(i-j+1==k){
                if(st.count(j))
                cnt--;
                if(cnt==0)
                ans++;
                j++;
            }
        }
        return ans;

    }
};
