class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        colors=colors+colors
        i=0
        res=0
        j=1
        while i<n:
            j=max(i+1,j)
            while j<len(colors) and colors[j]!=colors[j-1]:
                j+=1
            if j-i>=k:
                res+=1
            # print(i,j)
            i=i+1
        return res
