class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0 ; i < k-1 ; i++ ) colors.push_back(colors[i]) ;

        int n = colors.size() ;
        vector<int> pref(n+1,0) ;
        pref[0] = 0 ;
        for(int i = 1 ; i < n ; i++ ){
            pref[i] = pref[i-1] + (colors[i] != colors[i-1]) ;
        }
        int count = 0 ;
        for(int i = k-1 ; i < n ; i++ ){
            if( pref[i] - pref[i-(k-1)] == k-1 ) count++ ;
        }
        return count ;
    }
};
