class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {

        int N = nums.size(), l = 0, res = 0;


        for(int i=0; i<N; ++i) {

            bool good = true;

            for(int j=l; j<(i+k-1); ++j, ++l) {

                if(nums[j%N] == nums[(j+1)%N]) {
                    good = false;
                    i = j;
                    ++l;
                    break;
                }

            }


            if(good)
                res++;

        }

        return res;


    }
};
