class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        if(n<k) return 0;

        int c = 1;

        for(int i= 0; i<k-1; i++){
            v.push_back(v[i]);
            if(v[i]!=v[i+1]){
                continue;
            }
            else{
                c = 0;
            }
        }

        int ans = 0;
        c = 1;
        n = v.size();
        for(int i =1; i<n; i++){
            if(v[i]==v[i-1]){
                c=1;
            }
            else{
                c++;
            }
            if(c>=k){
                ans++;
            }
        }


        return ans;


    }
};
