class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size();
        vector<bool>check(n,true);
        for(int i=0;i<n-1;i++){
            if(v[i]==v[i+1]) check[i]=false;
        }
        if(v[n-1]==v[0]) check[n-1]=false;
        vector<int>index;
        for(int i=0;i<n;i++){
            if(check[i]==false) index.push_back(i);
        }
        int ans=0;
        if(index.size()==0) return n;
        // for(int i=0;i<index.size();i++){
        //     cout << index[i] << " ";
        // }
        // cout << endl;
        for(int i=0;i<index.size()-1;i++){
            if(index[i+1]-index[i]>=k){
                ans+=(index[i+1]-index[i]-k+1);
            }
        }
        int temp=(n-1-index[index.size()-1]);
        temp+=index[0]+1;
        // cout << ans << " " << temp << endl;
        if(temp>=k)
        ans+=(temp-k+1);
        return ans;

    }
};
