class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        for(int i=0;i<n;i++)v.push_back(v[i]);
        vector<int>dp(2*n,1);

        for(int i=v.size()-2;i>=0;i--){
            if(v[i]!=v[i+1]){
                dp[i] = dp[i+1]+1;
            }
            else{
                dp[i] = 1;
            }
        }
        int ans = 0;
        for(int i=0;i<n;i++){
            if(dp[i]>=k) ans++;
        }
        return ans;
    }
};
