class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt = 0;
        int sdc = 0;
        int n = colors.size();
        for(int i = 0;i<n+k-1;i++){
            if(i >= k){
                if(colors[(i-k)%n] != colors[(i-k+1)%n])sdc--;
                int val = colors[i%n];
                    int vv = colors[(i-1)%n];
                    sdc += val!=vv;
                cnt += sdc==k-1;
            }
            else{
                if(i){
                    int val = colors[i%n];
                    int vv = colors[(i-1)%n];
                    sdc += val!=vv;
                }
            }
            if(i == k-1)cnt += sdc==k-1;
        }
        return cnt;
    }
};
