class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int c=0;
        int x=0;
        int n=a.size();
        for(int i=0;i<k;i++){
            a.push_back(a[i]);
        }
        for(int i=0;i<n+k-1;i++){
            if(x==k-1){
                c++;
                x--;
            }
            if(a[i]!=a[i+1]){
                x++;
            }
            else{
                x=0;
            }
        }
    return c;
    }
};
