class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n=nums.size();
        int c=1;
        int ans=0;
        int j=0;
        int i=0;
        int r=0;
        while(j<n){


            if(nums[i]!=nums[(i+1)%n]){
                c++;
                if(c==k){
                    ans++;
                    c--;
                    j++;
                }
            }
            else{
                c=1;
                j=i+1;
                if(r>0){
                    break;
                }
            }

            if(i+1>=n){
                r++;

            }

             i=(i+1)%n;

        }

        return ans;
    }
};
