class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int ans = 0;

        int n = colors.size();
        vector<int> arr;

        for( int val: colors) {
            arr.push_back(val);
        }
        for( int val: colors) {
            arr.push_back(val);
        }

        for( int i=0; i<n; i++) {
            int j = i;
            while((j+1<arr.size()) && (arr[j+1]!=arr[j])) {
                j += 1;
            }
            while(i<=min(n-1,j)) {
                int l = j-i+1;
                ans += (l>=k);   
                i++;
            }
            i=j;
        }


        return ans;
    }
};
