class Solution {
    #define ll long long
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();

            ll ct=0;
        if(k==1)
            return n;
        else
        {
            ll chk=1;
            ll idx=1;
            while(idx<n+k-1)
            {
                ll curr=idx;
                ll prev=idx-1;
                if(curr>=n)
                    curr-=n;
                if(prev>=n)
                    prev-=n;
                if(colors[curr]!=colors[prev])
                    chk++;
                else
                    chk=1;
                if(chk>=k)
                    ct++;
                idx++;
            }
            return ct;
        }
    }
};
