class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ans = 0;
        int i = 0;
        int n = v.size();
        vector<int>dp(n + 1, -1);
        for(int i = 1; i <= n; i++){
            if(v[i % n] == v[i - 1]){
                dp[i] = i;
            }
            else{
                dp[i] = dp[i - 1];
            }
        }
        for(int i = 0; i + k <= n; i++){
            if(dp[i + k - 1] <= i){
                ans++;
            }
        }
        for(int i = n - k + 1; i < n; i++){
            if(dp[n] <= i && dp[(i + k - 1) % n] == -1){
                ans++;
            }
        }
        return ans;

    }
};
