class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0; i<n; i++){
            colors.push_back(colors[i]);
        }
        int cur = 1;
        int ans = 0;
        for(int i=0; i<min((n+k-2),2*n-1); i++){
            if(colors[i] != colors[i+1]){
                cur++;
            }
            else{
                cur = 1;
            }
            if(cur >= k){
                ans++;
            }
        }
        return ans;
    }
};
