class Solution {
public:
    int ok(vector<int>& brr, int tar){
        int temp = 0,i = 0,j = 1,len = brr.size();
        while (j < len + tar && i < len) {
            int one = j,two = one % len;
            while (brr[two]!=brr[(two-1+len)%len] && (one<len+tar-1)) {
                one += 1;two = one % len;
            }
            temp += max(one - i - tar + 1, 0);
            i = one;
            j = i + 1;
        }
        return temp;
    }
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        return ok(arr,k);
    }
};
