class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n=col.size();
        vector<int> suf(n,0);
        suf[n-1]=1;
        for(int i=n-2;i>=0;i--){
            if(col[i]!=col[i+1]) suf[i]=suf[i+1]+1;
            else suf[i]=1;
        }
        int ans = 0;
        for(int i=0;i<=n-k;i++){
             if(suf[i]>=k) ans++;
        }
        for(int i=n-k+1;i<n;i++){
            if(col[n-1]!=col[0] && suf[i]==n-i && suf[0]+suf[i]>=k)
             ans++;
        }
  return ans;
    }
};
