#define ll long long
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        ll n = colors.size();
        for(ll i=0; i<(k-1); i++){
            colors.push_back(colors[i]);
        }

        vector<ll> types;
        ll last = 0;
        ll i=1;
        for(; i<colors.size(); i++){
            if(colors[i] == colors[i-1]){
                types.push_back(i - last);
                last = i;
            }
        }
        types.push_back(i - last);

        ll cnt = 0;
        for(auto &it : types){
            if(it >= k){
                cnt += (it - k + 1);
            }
        }

        return cnt;
    }
};
