class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size() ;
        for(int i = 0 ; i < k - 1  ; i++) colors.push_back(colors[i]);
        vector<int>prefix(n+k+1,0);
        prefix[0]++;
        for(int i = 1 ; i < n+ k-1 ; i++){
              if(colors[i-1] == colors[i]) prefix[i] = 1 ;
              else prefix[i] = prefix[i-1]+1;
        }
        int ans =0 ;

        for(int i  = k-1 ; i < n+k-1 ; i++){
              if(prefix[i]  >= k) ans++;
        }
        return ans;
    }
};
