class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        if (k == 1) return colors.size();

        int count = 0, len = 1, N = colors.size();
        for (int i = 1; i < N + k - 1; ++i) {
            len = (colors[i % N] != colors[(i - 1) % N]) ? len + 1 : 1;
            count += (len >= k);
        }
        return count;
    }
};
