class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> a;
        for(int ele : colors) a.push_back(ele);
        for(int i=0;i<k-1;i++) a.push_back(colors[i]);
        int ans = 0;
        for(int i=0;i<a.size();i++) {
            int count = 1;
            for(int j=i+1;j<a.size();j++) {
                if(a[j] != a[j - 1]) count++;
                else {
                    i = j - 1;
                    break;
                }
                if(j == a.size() - 1) {
                    i = 3 * a.size();
                    break;
                }
            }
            ans += max(0, count - k + 1);
        }
        return ans;
    }
};
