class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& A, int k) {
        vector<int>arr=A;
        ll ans=0, n=arr.size();
        for(int i=0; i<n; i++){
            arr.pb(arr[i]);
        }
        // fr(2*n) cout<<arr[i]<<" "; nll;
        for(int i=0; i<n; i++){
            ll lst=i;
            for(int j=i+1; j<2*n; j++){
                // cout<<j<<" "<<arr[j]<<" "<<arr[j-1]<<endl;
                if(arr[j]==arr[j-1]){
                    lst=j-1;
                    break;
                }
                if(j==2*n-1) lst=2*n-1;
            }
            // cout<<i<<" "<<lst<<endl;
            ll now=(lst-i+1)-k+1;
            now = min (now,n-i);
            ans+=max(0ll,now);
            i=lst;
        }
        return ans;
    }
};
